import React, { useState, useEffect } from 'react';

function SumbitReview({ user, token }) {
  const [reviewData, setReviewData] = useState(null);

  useEffect(() => {
    const fetchReview = async () => {
      try {
        const response = await fetch("http://0.0.0.0:8000/d790c144-5da2-4ef5-b278-e4840b0349f3/review/", {
          method: 'GET',
          headers: {
		"Content-Type": "application/json",
            	Authorization: `Token ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setReviewData(data);
        } else {
          console.error('Failed to fetch game review');
        }
      } catch (error) {
        console.error('Network error:', error);
      }
    };

    fetchReview();
  }, [user, token]);

  return (
    <div>
      {reviewData ? (
        <div>
          <h2>Game Review</h2>
          <p>Username: {reviewData.username}</p>
          <p>Review: {reviewData.review || 'No review available'}</p>
        </div>
      ) : (
        <p>Loading review...</p>
      )}
    </div>
  );
}

export default SumbitReview;
