import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SubmitReview({ user, token }) {
  const [reviewData, setReviewData] = useState(null);

  useEffect(() => {
    const fetchReview = async () => {
      try {
        const response = await axios.get(`/d790c144-5da2-4ef5-b278-e4840b0349f3/review/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.status === 200) {
          setReviewData(response.data);
        } else {
          console.error('Failed to fetch game review');
        }
      } catch (error) {
        console.error('Network error:', error);
      }
    };

    fetchReview();
  }, [user, token]);

  return (
    <div>
      {reviewData ? (
        <div>
          <h2>Game Review</h2>
          <p>Username: {reviewData.username}</p>
          <p>Review: {reviewData.review || 'No review available'}</p>
        </div>
      ) : (
        <p>Loading review...</p>
      )}
    </div>
  );
}

export default SubmitReview;
