import styles from './App.module.css';
import useGames from "./hooks/useGames.js";
import { useEffect, useState } from "react";
import imdb from "./assets/imdb.png";
import SearchBar from "./components/SearchBar.jsx";
import Home from "./pages/Home.jsx";
import GameDetail from "./pages/GameDetail.jsx";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./pages/Login.jsx";
import Signup from "./pages/Signup.jsx";
import SubmitReview from "./pages/Review.jsx";
import ProtectedRoute from "./components/ProtectedRoute.jsx"


function App() {
	const [search, setSearch] = useState("");
	const token = localStorage.getItem("token");
        const user = localStorage.getItem("user");
        const [loginData, setLoginData] = useState({
                username: "",
                password: "",
        });
	  const [signupData, setSignupData] = useState({
		username: '',
		password1: '',
		password2: '',
	});
	const [loggedin, setlogin] = useState(false);
	const [isAuth, setAuth] = useState(false);
	if (token) {
		setAuth(true);
	}
	const isAuthenticated = localStorage.getItem('token') !== null;

	useEffect(() => {
		document.title = "IMDb Top 60 Video Games Ezra";
	});

	function getReviewPath() {
		if (isAuth) {
			return (
				<SubmitReview user={user} token={token} />
			);
		}
		else {
			return (
				<Login user={user} token={token} formData={loginData} setFormData={setLoginData} loggedin={loggedin} setlogin={setlogin}/>
			);
		}
	}

  return (
	  <>
	  <div className={styles.all}>
	  <div className={styles.navigation}>
	  <SearchBar search={search} setSearch={setSearch} user={user} token={token} formData={loginData} setFormDta={setLoginData} loggedin={loggedin} setlogin={setlogin}/>
	  </div>
	  <div className={styles.main}>
	  <Router>
	  <Routes>
	  	<Route index element={<Home search={search}/>} />
	  	<Route path="/:id" element={<GameDetail />} />
	  	<Route path="/login" element={<Login user={user} token={token} formData={loginData} setFormData={setLoginData} loggedin={loggedin} setlogin={setlogin}/>} />
	  	<Route path="/signup" element={<Signup user={user} token={token} formData={signupData} setFormData={setSignupData} loggedin={loggedin} setlogin={setlogin}/>} />
	  	<Route path="/review"  element={isAuth? <Login user={user} token={token} formData={loginData} setFormData={setLoginData} loggedin={loggedin} setlogin={setlogin}/> : <Signup user={user} token={token} formData={signupData} setFormData={setSignupData} loggedin={loggedin} setlogin={setlogin}/>} />
	  </Routes>
	  </Router>
	  </div>
	  <div className={styles.side}><span className={styles.yellowbox}></span>More to explore</div>
	  </div>
	  </>
  );
}

export default App;
