import React, { useState, useContext, useRef, useEffect } from 'react';
import axios from 'axios';
import styles from "../App.module.css";
import AuthContext from "../context/AuthProvider";
import { useNavigate } from 'react-router-dom';

interface Props {
	user: string;
	token: string;
	formData: {};
	setFormData: () => void;
	loggedin: boolean;
	setlogin: () => void;
}

function Signup( {user, token, formData, setFormData, loggedin, setlogin} : Props) {
	const { setAuth } = useContext(AuthContext);
	const userRef = useRef();
	const errRef = useRef();

	const [errMsg, setErrMsg] = useState("");
	const [success, setSuccess] = useState(false);
	const navigate = useNavigate();


	useEffect(() => {
		if (user) {
		setSuccess(true);
		setlogin(true);
		navigate("/");
	}

		userRef.current.focus();
	}, [])
	useEffect(() => {
		setErrMsg("")
	}, [formData.username, formData.password])

	const handleChange = (e) => {
		const { name, value } = e.target;
		setFormData({
			...formData,
			[name]: value,
		});
	};

	const handleSubmit = async (e) => {
    		e.preventDefault();

		try {
			const response = await axios.post('http://localhost:8000/user/dj-rest-auth/registration/', formData);
			const token = response?.data?.key
			console.log(response.data);
			console.log(token);
			console.log(formData.username);
			console.log(formData.password);
			const user = formData.username;
			const pwd = formData.password
			console.log('Signup successful:', response.data);
			setAuth({ user, pwd, token});
			localStorage.setItem("user", user)
			localStorage.setItem("token", token);
			setSuccess(true);
			setlogin(true);
			navigate("/");

		} catch (err) {
		}
	};
	function remover() {
		localStorage.removeItem("user");
		localStorage.removeItem("token");
		setSuccess(false);
		setlogin(false);
	}

	return (
		<>
	  			{success ? (
				<div>
				<h1>You are Signed Up!</h1>
				<br />
				<p><button onClick={remover}>Log Out</button></p>
				</div>
			) : (
	    <div className={styles.login}>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
	<div>
          <label>Username:</label>
          <input
            type="text"
            name="username"
		ref={userRef}
            value={formData.username}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password1"
            value={formData.password1}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Confirm Password:</label>
          <input
            type="password"
            name="password2"
            value={formData.password2}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
		)}
	</>
  );
}

export default Signup;
