import useGames from "../hooks/useGames.js";
import styles from "../App.module.css";

function Home() {
const {games, error, isLoading } = useGames();

return (
	<>
	<div className={styles.topmain}>
	<div className={styles.toptitle}> IMDb Charts </div>
	<div className={styles.hometitle}>IMDB Top 60 VideoGames</div>
	<div className={styles.bottomtitle}>IMDB Top 60 as rated by regular IMDb votersi</div>
	</div>
	<div>
	<ol>
	{games.map(game =>
		(<li key={game.id}>
			<a href={`/${game.id}`}>{game.title}</a></li>
	))}
	</ol>
	</div>
	</>
);
};

export default Home;
