import React, { useState } from 'react';
import YouTube from 'react-youtube';
import CustomPosterOverlay from './Poster.jsx';
import styles from "../App.module.css";

interface Props {
	poster : string;
	film : string;
}

const VideoPlayer = ( {poster, film}: Props) => {
  const [videoState, setVideoState] = useState('unstarted');

  const opts = {
    playerVars: {
      autoplay: 1,
    },
  };

	console.log(film)
  const videoId = "EE-4GvjKcfs&t=6s";

  const onVideoStateChange = (event) => {
    setVideoState(event.data);
  };

  return (
    <div>
      {videoState !== 1  && (
        <CustomPosterOverlay poster={poster}/>
      )}
      <YouTube className={styles.detailtrailer} videoId={videoId} opts={opts} onStateChange={onVideoStateChange} />
    </div>
  );
};

export default VideoPlayer;
