import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
import addwatchlist from "../assets/addwatchlist.png";
import styles from "../App.module.css";
import watchlistimage from "../assets/watchlist.png";

interface Props {
	user: string;
	token: string;
	setWatchlistOn: () => void;
	watchlistSuccess: boolean;
	setWatclistSuccess: () => void;
	watchlist: boolean;
	setWatchlist: () => void;
}

function Watchlist({ user, token, watchlistSuccess, setWatchlistSuccess, setWatchlistOn, watchlist, setWatchlist}: Props) {
	const { id } = useParams();
	const watchlistData = {
		your_watchlist: watchlist,
	};

	const handleRemove = async (e) => {
		e.preventDefault();

		setWatchlistOn(false);

		try {
			const response = await axios.delete(`http://0.0.0.0:8000/${id}/rating/`,
        		{
				headers: {
				'Content-Type': 'application/json',
				Authorization: `Token ${token}`,
			},
			}
		);

	if (response.status === 200) {
		console.log('Watchlist removed successfully');
		setWatchlistSuccess(true);
	} else {
		console.error('Watchlist remove failed');
	}
	} catch (error) {
		console.error('Network error:', error);
	}
		window.location.reload();
	};

	const handleSubmit = async () => {


		try {
			const response = await axios.put(`http://0.0.0.0:8000/${id}/watchlist/`, watchlistData,
        		{
				headers: {
				'Content-Type': 'application/json',
				Authorization: `Token ${token}`,
			},
			}
		);

	if (response.status === 200) {
		console.log('Watchlist Added successfully');
		setWatchlistSuccess(true);
	} else {
		console.error('Watchlist failed');
	}
	} catch (error) {
		console.error('Network error:', error);
	}
	};
	if (watchlist) {
		handleSubmit();
	}

	return (
		<div onClick={handleRemove}>
		<img className={styles.watchlistimage} src={watchlistimage} />
		</div>
	);
}

export default Watchlist;
