import React, { useState } from "react";
import styles from "../App.module.css";

interface Props {
	onSelectSearch: (searchselector: SearchSelector) => void;
}

function SearchSelector({ onSelectSearch} : Props) {
	const searchs = ["All", "Title", "Tv Episode", "Celebs", "Companies", "Keyword"];

	function handleOptionChange(event) {
		onSelectSearch(event.target.value);
	};
	return (
		<>
		<label htmlFor="platform"></label>
		<select className={styles.dropdownsearch}id="platform" value={""} onChange={handleOptionChange}>
			<option className={styles.droplistsearch} value="">All</option>
			<div>{searchs.map((option, index) => (
				<option className={styles.droplistsearch} key={index} value={option.title}>
				{option}
				</option>
			))}</div>
		</select>
		</>
	);
};

export default SearchSelector;
