	poster : string;
	film : string;
}

const VideoPlayer = ( {poster, film}: Props) => {
  const [videoState, setVideoState] = useState('unstarted');

  const opts = {
    playerVars: {
      autoplay: 1,
    },
  };

  const videoId = film;

	const onVideoStateChange = (event) => {
		setVideoState(event.data);
		  console.log(videoState);
};
function P() {
};

  return (
    <div className={styles.Player}>
      <YouTube className={styles.detailtrailer} videoId={videoId} opts={opts} onStateChange={onVideoStateChange} />
      {videoState !== 1  && (
        <span onClick={()=> {setVideoState('3')}}><CustomPosterOverlay poster={poster}/></span>
      )}
    </div>
  );
};

export default VideoPlayer;
