import styles from './App.module.css';
import useGames from "./hooks/useGames.js";
import { useEffect, useState } from "react";
import imdb from "./assets/imdb.png";
import SearchBar from "./components/SearchBar.jsx";
import Home from "./pages/Home.jsx";
import GameDetail from "./pages/GameDetail.jsx";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./pages/Login.jsx";
import Signup from "./pages/Signup.jsx";


function App() {
	const [search, setSearch] = useState("");
	const token = localStorage.getItem("token");
        const user = localStorage.getItem("user");
        const [formData, setFormData] = useState({
                username: "",
                password: "",
        });
	const [loggedin, setlogin] = useState(false);
	useEffect(() => {
		document.title = "IMDb Top 60 Video Games Ezra";
	});

  return (
	  <>
	  <div className={styles.all}>
	  <div className={styles.navigation}>
	  <SearchBar search={search} setSearch={setSearch} user={user} token={token} formData={formData} setFormDta={setFormData} loggedin={loggedin} setlogin={setlogin}/>
	  </div>
	  <div className={styles.main}>
	  <Router>
	  <Routes>
	  	<Route index element={<Home search={search}/>} />
	  	<Route path="/:id" element={<GameDetail />} />
	  	<Route path="/login" element={<Login user={user} token={token} formData={formData} setFormData={setFormData} loggedin={loggedin} setlogin={setlogin}/>} />
	  	<Route path="/signup" element={<Signup user={user} token={token} formData={formData} setFormData={setFormData} loggedin={loggedin} setlogin={setlogin}/>} />
	  </Routes>
	  </Router>
	  </div>
	  <div className={styles.side}><span className={styles.yellowbox}></span>More to explore</div>
	  </div>
	  </>
  );
}

export default App;
