import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
import starfilled from "../assets/starfilled.png";
import styles from "../App.module.css";

interface Props {
	user: string;
	token: string;
	setWatchlistOn: () => void;
	watchlistSuccess: boolean;
	setWatclistSuccess: () => void;
}

function Watchlist({ user, token, watchlistSuccess, setWatchlistSuccess, setWatchlistOn}: Props) {
	const { id } = useParams();
	const [watchlist, setWatchlist] = useState(false);
	const watchlistData = {
		your_watchlist: watchlist,
	};

	const handleSubmit = async (e) => {
		e.preventDefault();


		try {
			const response = await axios.put(`http://0.0.0.0:8000/${id}/watchlist/`, watchlistData,
        		{
				headers: {
				'Content-Type': 'application/json',
				Authorization: `Token ${token}`,
			},
			}
		);

	if (response.status === 200) {
		console.log('Watchlist Added successfully');
		setWatchlistSuccess(true);
	} else {
		console.error('Watchlist failed');
	}
	} catch (error) {
		console.error('Network error:', error);
	}
	};

	return (
		<>
		</>
	);
}

export default Watchlist;
