import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";

function Rate({ user, token }) {
	const { id } = useParams();
	const [rate, setRate] = useState(null);
	const [rateSuccess, setRateSuccess] = useState(false);

	const handleSubmit = async (e) => {
		e.preventDefault();

		const rateData = {
			your_rating: rate,
		};

		try {
			const response = await axios.put(`http://0.0.0.0:8000/${id}/rating/`, rateData,
        		{
				headers: {
				'Content-Type': 'application/json',
				Authorization: `Token ${token}`,
			},
			}
		);

	if (response.status === 200) {
		console.log('Rated successfully');
		setReviewSuccess(true);
	} else {
		console.error('Rate failed');
	}
	} catch (error) {
		console.error('Network error:', error);
	}
	};

	return (
		<>
		{rateSuccess ? (
			<>
			<div>You have rated the game </div>
			<div>{rateData.your_rating}</div>
			</>
		) : (
	<div>
	<h2>Rate The Game</h2>
	<select onChange={(e) => setReview(e.target.value)}  value={rate || 'x'}>
			<option value="x">X</option>
			<option value={1}>*</option>
			<option value={2}>**</option>
			<option value={3}>***</option>
			<option value={4}>****</option>
			<option value={5}>*****</option>
			<option value={6}>******</option>
			<option value={7}>*******</option>
			<option value={8}>********</option>
			<option value={9}>*********</option>
			<option value={10}>**********</option>
			</select>
			<p>Selected Value: {rate}</p>
	</div>
		) }
		</>
	);
}

export default Rate;
